# decompile_from_marshal.pyimport marshal, iofrom decompyle3.main import decompile_code# baca payload marshal yang tadi kamu simpanwith open("bytecode.marshal", "rb") as f:    payload = f.read()# ubah jadi code objectcode_obj = marshal.loads(payload)# decompile jadi source code (string)src = decompile_code(code_obj)# simpan ke filewith open("hasil_auto.py", "w", encoding="utf-8") as out:    out.write(src)# optional: print sedikit ke layar biar kelihatan berhasilprint("OK -> hasil_auto.py")